import React, { Component } from "react";
import CV from "./Components/CV/CV";
import Inputs from "./Components/Inputs";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      cv_data: {
        about: {
          name: "",
          surname: "",
          email: "",
          address: "",
          phone: "",
          description: ""
        },
        education: {
          counter: 1,
          institutions: [
            {
              name: "",
              start: "",
              end: ""
            }
          ]
        },
        experience: {
          counter: 1,
          institutions: [
            {
              name: "",
              position: "",
              start: "",
              end: ""
            }
          ]
        },
        extraInfo: {
            skills: [],
            languages: []
          }
      }
    };
  };

  handleChange = (input) => {
    switch(input.target.name) {
      case "name":
        this.setState(
          { 
            cv_data: {...this.state.cv_data, 
              about: {...this.state.cv_data.about,
                name: input.target.value
              }
            }
          }
        );
        break;
      case "surname":
        this.setState(
          { cv_data: {...this.state.cv_data, 
              about: {...this.state.cv_data.about,
                surname: input.target.value
              }
            }
          }
        );
        break;
      case "email":
        this.setState(
          { cv_data: {...this.state.cv_data, 
              about: {...this.state.cv_data.about,
                email: input.target.value
              }
            }
          }
        );
        break;
      case "address":
        this.setState(
          { cv_data: {...this.state.cv_data, 
              about: {...this.state.cv_data.about,
                address: input.target.value
              }
            }
          }
        );
        break;
      case "phone":
        this.setState(
          { cv_data: {...this.state.cv_data, 
              about: {...this.state.cv_data.about,
                phone: input.target.value
              }
            }
          }
        );
        break;
      case "description":
        this.setState(
          { cv_data: {...this.state.cv_data ,
              about: {...this.state.cv_data.about,
                description: input.target.value
              }
            }
          }
        );
        break;
      case "eduName":
        this.setState(
          { cv_data: {...this.state.cv_data, 
              education: {...this.state.cv_data.education,
                institutions: [
                  {
                    ...this.state.cv_data.education.institutions[0],
                    name: input.target.value,
                  }
                ]
              }
            }
          }
        );
        break;
      case "startDate":
          this.setState(
            { cv_data: {...this.state.cv_data, 
                education: {...this.state.cv_data.education,
                  institutions: [
                    {
                      ...this.state.cv_data.education.institutions[0],
                      start: input.target.value,
                    }
                  ]
                }
              }
            }
          );
        break;
      case "graduationDate":
        this.setState(
          { cv_data: {...this.state.cv_data, 
              education: {...this.state.cv_data.education,
                institutions: [
                  {
                    ...this.state.cv_data.education.institutions[0],
                    end: input.target.value,
                  }
                ]
              }
            }
          }
        );
        break;
      case "expName":
        this.setState(
          { cv_data: {...this.state.cv_data, 
              experience: {...this.state.cv_data.experience,
                institutions: [
                  {
                    ...this.state.cv_data.experience.institutions[0],
                    name: input.target.value,
                  }
                ]
              }
            }
          }
        );
        break;
      case "posOccupied":
        this.setState(
          { cv_data: {...this.state.cv_data, 
              experience: {...this.state.cv_data.experience,
                institutions: [
                  {
                    ...this.state.cv_data.experience.institutions[0],
                    position: input.target.value,
                  }
                ]
              }
            }
          }
        );
        break;
      case "workStart":
        this.setState(
          { cv_data: {...this.state.cv_data, 
              experience: {...this.state.cv_data.experience,
                institutions: [
                  {
                    ...this.state.cv_data.experience.institutions[0],
                    start: input.target.value,
                  }
                ]
              }
            }
          }
        );
        break;
      case "workEnd":
        this.setState(
          { cv_data: {...this.state.cv_data, 
              experience: {...this.state.cv_data.experience,
                institutions: [
                  {
                    ...this.state.cv_data.experience.institutions[0],
                    end: input.target.value,
                  }
                ]
              }
            }
          }
        );
        break;
      case "skills":
        this.setState(
          { cv_data: {...this.state.cv_data,
              extraInfo: {...this.state.cv_data.extraInfo,
                skills: [input.target.value],
              }
            }
          }
        );
        break;
      case "languages":
        this.setState(
          { cv_data: {...this.state.cv_data,
              extraInfo: {...this.state.cv_data.extraInfo,
                languages: [input.target.value],
              }
            }
          }
        );
        break;
      default: 
        break;
    }
  }

  addInputs = (type) => {
    switch(type.target.id) {
      case "addEdu":

        break;
      default:
        break;
    }
  }

  render() {

    const { cv_data } = this.state;
    const eduInputs = [];
    const jobInputs = [];
    for (let i = 0; i < cv_data.education.counter; i++) {
        eduInputs.push(
          [
            `<label htmlFor="eduName">Institution's name: </label>`,
            `<input id="eduName" name="eduName" onChange={e => handleChange(e)} />`,
            `<label htmlFor="startDate">Start date: </label>`,
            `<input type="date" id="startDate" name="startDate" onChange={e => handleChange(e)} />`,
            `<label htmlFor="graduationDate">Graduation date: </label>`,
            `<input type="date" id="graduationDate" name="graduationDate" onChange={e => handleChange(e)} />`,
          ]
        );
    }

    return (
      <div className="App">
        <Inputs eduInputs={eduInputs} addInputs={this.addInputs} handleChange={this.handleChange} />
        <CV data={cv_data} />
      </div>
    );
  }
}

export default App;
